/**
 * Renders the Menu Grid based on the current data and active filter.
 * Handles the creation of cards, badges, and admin controls.
 */
function renderMenu() {
    const grid = document.getElementById('menu-grid');
    if (!grid) {
        console.error("Error: Menu Grid element not found.");
        return;
    }
    
    // Clear the current grid to prevent duplicates
    grid.innerHTML = '';
    
    // Determine the active category filter
    const activeBtn = document.querySelector('.tab-btn.active');
    let activeCat = 'brownie'; // Default
    
    if (activeBtn) {
        // Extract category from onclick attribute (e.g., "filterMenu('jar')")
        const match = activeBtn.getAttribute('onclick').match(/'([^']+)'/);
        if (match) {
            activeCat = match[1];
        }
    }

    // Loop through data and build HTML
    menuData.forEach((item, index) => {
        const card = document.createElement('div');
        card.className = `tilt-card ${item.cat}`;
        
        // Filter Logic: Hide items that don't match the active category
        if (item.cat !== activeCat) {
            card.style.display = 'none';
        }

        // Generate Badge HTML only if a badge exists
        const badgeHTML = item.badge ? `<div class="badge">${item.badge}</div>` : '';
        
        // Truncate Ingredients for display (Expanded view shows full list)
        const displayIng = item.ing 
            ? (item.ing.length > 35 ? item.ing.substring(0, 35) + "..." : item.ing) 
            : "Premium Ingredients";

        // Generate Admin Controls (Edit/Delete) - Visual only if admin is active
        // The CSS 'body.admin-active .admin-btn-group' handles visibility
        const adminBtns = `
            <div class="admin-btn-group">
               <button class="edit-btn" onclick="openEditModal(${index})" title="Edit Item"><i class="fa-solid fa-pencil"></i></button>
                <button class="delete-btn" onclick="deleteItem(${index})" title="Delete Item">X</button>
            </div>
        `;

        // Inject Content
        card.innerHTML = `
            ${badgeHTML}
            ${adminBtns}
            <div class="card-visual" style="background-image: url('${item.img}');"></div>
            <div class="card-content">
                <h3>${item.name}</h3>
                <p>${item.desc}</p>
                <p class="ingredients-text"><b>Inside:</b> ${displayIng}</p>
                <button class="action-btn" onclick="openProductModal('${item.name}')">VIEW & ORDER</button>
            </div>
        `;
        
        grid.appendChild(card);
    });
    
    // Re-initialize 3D tilt effect for new elements
    applyTiltEffect();
}

/**
 * Renders the Reviews section horizontally.
 */
function renderReviews() {
    const grid = document.getElementById('reviews-grid');
    if (!grid) return;
    
    grid.innerHTML = ''; // Clear existing
    
    reviewsData.forEach(rev => {
        // Create Star String (e.g., "")
        const stars = "".repeat(rev.stars) + "".repeat(5 - rev.stars);
        
        const card = document.createElement('div');
        card.className = "glass-review hover-trigger";
        card.innerHTML = `
            <div class="stars">${stars}</div>
            <p>"${rev.text}"</p>
            <cite> ${rev.name}</cite>
        `;
        grid.appendChild(card);
    });
}

