/**
 * Sets the global website theme.
 * @param {string} theme - 'midnight', 'espresso', or 'velvet'
 */
function setTheme(theme) {
    // 1. Clean slate: Remove all theme classes
    document.body.classList.remove('theme-midnight', 'theme-espresso', 'theme-velvet');
    
    // 2. Apply new theme (Midnight is default/base style, so no class needed)
    if (theme !== 'midnight') {
        document.body.classList.add(`theme-${theme}`);
    }
    
    // 3. Ensure Admin Mode persists visually if logged in
    if (isAdmin) {
        document.body.classList.add('admin-active');
    }
    
    // 4. Save to Browser Storage
    localStorage.setItem('brokenBrownieTheme', theme);
}

/**
 * Handles the Custom Cursor logic and Scroll Effects.
 * Only runs on Desktop to preserve Mobile performance.
 */
function setupCursor() {
    const dot = document.querySelector('.cursor-dot');
    const ring = document.querySelector('.cursor-ring');

    // Check if device uses a fine pointer (Mouse) vs Coarse (Touch)
    if (window.matchMedia("(pointer: fine)").matches) {
        let mouseX = 0, mouseY = 0;
        let ringX = 0, ringY = 0;

        window.addEventListener('mousemove', e => {
            mouseX = e.clientX;
            mouseY = e.clientY;
            
            // Dot follows instantly
            if (dot) {
                dot.style.left = `${mouseX}px`;
                dot.style.top = `${mouseY}px`;
            }
        });

        // Ring follows with delay (Lerp)
        const animate = () => {
            ringX += (mouseX - ringX) * 0.15;
            ringY += (mouseY - ringY) * 0.15;
            
            if (ring) {
                ring.style.left = `${ringX}px`;
                ring.style.top = `${ringY}px`;
            }
            requestAnimationFrame(animate);
        };
        animate();

        // Hover Effect Listener
        document.body.addEventListener('mouseover', e => {
            if (e.target.closest('.hover-trigger') || 
                e.target.closest('button') || 
                e.target.closest('a') || 
                e.target.closest('input')) {
                document.body.classList.add('hovering');
            } else {
                document.body.classList.remove('hovering');
            }
        });
    }
}

function setupScrollEffects() {
    const nav = document.querySelector('.glass-nav');
    window.addEventListener('scroll', () => {
        if (window.scrollY > 50) {
            nav.classList.add('scrolled');
        } else {
            nav.classList.remove('scrolled');
        }
    });
}

