function checkLogin() {
    const passField = document.getElementById('admin-pass');
    const pass = passField.value;
    
    // SECURITY CHECK
    if (pass === 'brownie123') { 
        isAdmin = true;
        document.body.classList.add('admin-active');
        document.getElementById('admin-controls').classList.remove('hidden');
        
        closeModal('login-modal');
        passField.value = ''; // Clear password field
        showToast("Welcome back, Owner!");
    } else {
        showToast("Invalid Passcode ");
        passField.classList.add('shake-error'); // Add visual shake logic if CSS exists
        setTimeout(() => passField.classList.remove('shake-error'), 500);
    }
}

function adminLogout() {
    isAdmin = false;
    document.body.classList.remove('admin-active');
    document.getElementById('admin-controls').classList.add('hidden');
    showToast("Logged Out Successfully.");
}

// --- ADD ITEM LOGIC ---
function openAddModal() { 
    // Reset all fields
    const fields = ['new-name', 'new-desc', 'new-ing', 'new-img', 'new-badge'];
    fields.forEach(id => {
        const el = document.getElementById(id);
        if (el) el.value = '';
    });
    document.getElementById('add-modal').classList.remove('hidden'); 
}

function saveNewItem() {
    // Gather Data
    const name = document.getElementById('new-name').value;
    const desc = document.getElementById('new-desc').value;
    const ing = document.getElementById('new-ing').value;
    const img = document.getElementById('new-img').value || "https://source.unsplash.com/random/?dessert";
    const cat = document.getElementById('new-cat').value;
    const badge = document.getElementById('new-badge').value;
    
    // Validation
    if (name && desc && cat) {
        // Add to Array
        menuData.push({ name, desc, ing, img, cat, badge });
        
        // Save to Persistent Storage
        localStorage.setItem('brokenBrownieMenu', JSON.stringify(menuData));
        
        // Refresh UI
        renderMenu();
        closeModal('add-modal');
        filterMenu(cat); // Switch to the new item's category
        showToast("New Item Added Successfully! ");
    } else {
        showToast("Error: Name, Description and Category are required.");
    }
}

// --- EDIT ITEM LOGIC ---
function openEditModal(index) {
    const item = menuData[index];
    if (!item) return;

    // Populate Modal with Existing Data
    document.getElementById('edit-index').value = index;
    document.getElementById('edit-name').value = item.name;
    document.getElementById('edit-desc').value = item.desc;
    document.getElementById('edit-ing').value = item.ing || "";
    document.getElementById('edit-img').value = item.img;
    document.getElementById('edit-cat').value = item.cat;
    document.getElementById('edit-badge').value = item.badge || "";
    
    document.getElementById('edit-modal').classList.remove('hidden');
}

function saveEditedItem() {
    const index = document.getElementById('edit-index').value;
    
    // Update Data Object
    menuData[index] = {
        name: document.getElementById('edit-name').value,
        desc: document.getElementById('edit-desc').value,
        ing: document.getElementById('edit-ing').value,
        img: document.getElementById('edit-img').value,
        cat: document.getElementById('edit-cat').value,
        badge: document.getElementById('edit-badge').value
    };
    
    // Save & Refresh
    localStorage.setItem('brokenBrownieMenu', JSON.stringify(menuData));
    renderMenu();
    closeModal('edit-modal');
    showToast("Item Updated Successfully!");
}

function deleteItem(index) {
    if (confirm("Are you sure you want to permanently delete this item?")) {
        menuData.splice(index, 1); // Remove from array
        localStorage.setItem('brokenBrownieMenu', JSON.stringify(menuData));
        renderMenu();
        showToast("Item Deleted.");
    }
}

