function addToCart(itemName) {
    cart.push(itemName);
    localStorage.setItem('brokenBrownieCart', JSON.stringify(cart));
    
    updateCartUI();
    closeModal('product-modal');
    showToast(`${itemName} added to bag! `);
}

/**
 * Updates the floating cart button visibility and count.
 */
function updateCartUI() {
    const cartEl = document.getElementById('smart-bag');
    const countEl = document.querySelector('.cart-count');
    
    if (!cartEl || !countEl) return;
    
    if (cart.length > 0) {
        cartEl.classList.remove('hidden');
        countEl.innerText = `${cart.length} ITEMS`;
        
        // Trigger small bounce animation
        cartEl.style.animation = 'none';
        cartEl.offsetHeight; // trigger reflow
        cartEl.style.animation = 'slideUp 0.5s ease';
    } else {
        cartEl.classList.add('hidden');
    }
}

/**
 * Opens the "Review Order" Modal with a list of items.
 */
function openCartModal() {
    const list = document.getElementById('cart-items-list');
    if (!list) return;
    
    list.innerHTML = '';
    
    if (cart.length === 0) {
        list.innerHTML = '<p style="color:var(--text-muted); text-align:center; padding:20px;">Your bag is currently empty.</p>';
    } else {
        cart.forEach((item, index) => {
            const row = document.createElement('div');
            // Inline CSS for the receipt item layout
            row.style.cssText = `
                display: flex; 
                justify-content: space-between; 
                align-items: center; 
                padding: 12px 0; 
                border-bottom: 1px solid rgba(255,255,255,0.1);
            `;
            
            // Item Row with Remove Button
            row.innerHTML = `
                <div style="display:flex; align-items:center; gap:10px;">
                    <span style="color:var(--accent); font-weight:bold;">${index + 1}.</span>
                    <span style="color:white;">${item}</span>
                </div>
                <button onclick="removeFromCart(${index})" 
                        style="color: #ff6b6b; background:none; border:none; cursor:pointer; font-size:0.8rem; text-decoration:underline;">
                    Remove
                </button>
            `;
            list.appendChild(row);
        });
    }
    document.getElementById('cart-modal').classList.remove('hidden');
}

function removeFromCart(index) {
    cart.splice(index, 1); // Remove item
    localStorage.setItem('brokenBrownieCart', JSON.stringify(cart)); // Update storage
    
    // Logic: If empty, close modal. If items remain, refresh list.
    if (cart.length === 0) {
        closeModal('cart-modal');
    } else {
        openCartModal(); 
    }
    
    updateCartUI();
    showToast("Item removed from bag.");
}

/**
 * Generates WhatsApp Message and Clears Cart.
 */
function checkoutWhatsApp() {
    if (cart.length === 0) {
        showToast("Your bag is empty!");
        return;
    }
    
    // Format the list neatly
    const orderList = cart.map((item, index) => ` ${item}`).join('\n');
    const msg = `*NEW ORDER REQUEST* \n------------------\n${orderList}\n------------------\n\nI want to customize my order`;
    
    // Open WhatsApp API
    window.open(`https://wa.me/919789028598?text=${encodeURIComponent(msg)}`, '_blank');
    
    // Clear Cart (Only after successful checkout click)
    cart = [];
    localStorage.removeItem('brokenBrownieCart');
    updateCartUI();
    closeModal('cart-modal');
}

/* --------------------------------------------------------------------------
   SECTION 8: UI UTILITIES (TOASTS, FILTERS, MODALS)
   -------------------------------------------------------------------------- */

// Shows a non-intrusive notification popup
function showToast(msg) {
    const container = document.getElementById('toast-container');
    if (!container) return;
    
    const toast = document.createElement('div');
    toast.className = 'toast';
    toast.innerHTML = `<span>${msg}</span>`;
    
    container.appendChild(toast);
    
    // Animate In/Out
    setTimeout(() => {
        toast.style.opacity = '0';
        setTimeout(() => toast.remove(), 300);
    }, 3500);
}

// Category Filter Logic
function filterMenu(cat) {
    // 1. Update Buttons Visual State
    document.querySelectorAll('.tab-btn').forEach(b => b.classList.remove('active'));
    
    const btns = Array.from(document.querySelectorAll('.tab-btn'));
    const targetBtn = btns.find(b => b.getAttribute('onclick').includes(`'${cat}'`));
    
    if (targetBtn) {
        targetBtn.classList.add('active');
    }
    
    // 2. Re-render Grid
    renderMenu();
}

// Live Search Filter
function filterSearch() {
    const input = document.getElementById('spotlight-search').value.toLowerCase();
    const cards = document.querySelectorAll('.tilt-card');
    
    cards.forEach(card => {
        const title = card.querySelector('h3').innerText.toLowerCase();
        
        if (title.includes(input)) {
            card.style.display = 'block';
        } else {
            card.style.display = 'none';
        }
    });
}

function closeModal(id) { 
    const modal = document.getElementById(id);
    if (modal) modal.classList.add('hidden'); 
}

/**
 * Opens Detailed Product View
 * Populates Name, Desc, Ingredients, and Image
 */
function openProductModal(itemName) {
    const item = menuData.find(i => i.name === itemName);
    if (!item) return;
    
    // Populate Data
    document.getElementById('view-img').src = item.img;
    document.getElementById('view-name').innerText = item.name;
    document.getElementById('view-desc').innerText = item.desc;
    document.getElementById('view-badge').innerText = item.badge || "FRESH BAKED";
    
    // Handle Ingredients
    const ingText = document.querySelector('.ingredients-text-view');
    if (ingText) {
        ingText.innerHTML = item.ing ? `<b>Ingredients:</b> ${item.ing}` : "";
    }

    // Bind Add Button (Clone to remove old listeners)
    const btn = document.getElementById('view-add-btn');
    const newBtn = btn.cloneNode(true);
    btn.parentNode.replaceChild(newBtn, btn);
    
    newBtn.onclick = function() { addToCart(item.name); };
    
    document.getElementById('product-modal').classList.remove('hidden');
}

// --- REVIEW SUBMISSION ---
function openReviewModal() { 
    document.getElementById('review-modal').classList.remove('hidden'); 
}

function submitReview() {
    const name = document.getElementById('review-name').value;
    const text = document.getElementById('review-text').value;
    const rating = document.getElementById('review-rating').value;
    
    if (name && text) {
        // Add to top of array
        reviewsData.unshift({ name, text, stars: parseInt(rating) });
        
        // Save & Refresh
        localStorage.setItem('brokenBrownieReviews', JSON.stringify(reviewsData));
        renderReviews();
        closeModal('review-modal');
        showToast("Review Submitted Successfully! ");
    } else {
        showToast("Please fill all fields.");
    }
}

// --- 3D TILT EFFECT ---
function applyTiltEffect() {
    // Only apply on Desktop to save mobile battery and avoid weird touch interactions
    if (window.innerWidth < 768) return; 
    
    document.querySelectorAll('.tilt-card').forEach(card => {
        card.onmousemove = (e) => {
            const rect = card.getBoundingClientRect();
            const x = e.clientX - rect.left; 
            const y = e.clientY - rect.top;
            
            const centerX = rect.width / 2; 
            const centerY = rect.height / 2;
            
            // Calculate rotation (Limit max rotation to 8 degrees)
            const rotateX = ((y - centerY) / centerY) * -8;
            const rotateY = ((x - centerX) / centerX) * 8;
            
            card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale(1.02)`;
        };
        
        // Reset on mouse leave
        card.onmouseleave = () => {
            card.style.transform = 'perspective(1000px) rotateX(0) rotateY(0) scale(1)';
        };
    });
}

// --- VISITOR ANALYTICS ---
async function trackVisitor() {
    // Determine Device Type
    const deviceType = window.innerWidth < 768 ? 'Mobile' : 'Desktop';
    
    try {
        // Send data to backend (Localhost default)
        // Ensure your server.js is running on port 5000
        await fetch('http://localhost:5000/api/track', { 
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ device: deviceType })
        });
        console.log("Visitor Tracked Successfully.");
    } catch (err) { 
        // Fail silently if server is offline (doesn't break the site)
        console.warn("Tracking skipped (Server Offline)."); 
    }
}

